import { React, useEffect } from 'react';
import  ReactDOM  from 'react-dom';
import styles from './OverlayMenu.module.css';
import { connect } from 'react-redux';
import { overSelector } from '../store/overlay/overSelectors';
import { updateOver } from '../store/overlay/overActions';
import store from '../store/store';

export const OverlayComponent = ({}) => {


    useEffect(() => {
        () => changeOver();
    }, [])

    return (

        <div className={styles.container}>
            <div className={styles.cross}>
                <img src="https://img.icons8.com/ios-glyphs/30/000000/delete-sign.png"/>
            </div>
            <div className={styles.links}>
                <a href="#">Menu</a>
                <a href="#">Votre panier</a>
                <a href="#">Se connecter</a>
            </div>
        </div>

    );

}

export const OverlayMenuComponent = ({ changeOver }) => {


    return (
        <>
            {ReactDOM.createPortal(
                <OverlayComponent OverHandler={changeOver} />, 
                document.getElementById('overlay-menu'))}
        </>
    );

}

export const OverlayMenu = connect(
    (state) => ({
        over: overSelector(state)
    }),
    (dispatch) => ({
        changeOver: dispatch(updateOver())
    })
)(OverlayMenuComponent)
