import { React } from 'react';
import  ReactDOM  from 'react-dom';
import styles from './OverlayMenu.module.css';
import { connect } from 'react-redux';
import { overSelector } from '../store/overlay/overSelectors';
import { updateOver } from '../store/overlay/overActions';
import store from '../store/store';

export const OverlayComponent = ({props, changeOver}) => {


    console.log(props.over);

    return (

        <div className={styles.container}>
            <div className={styles.cross}>
                <img onClick={() => changeOver()} src="https://img.icons8.com/ios-glyphs/30/000000/delete-sign.png"/>
            </div>
            <div className={styles.links}>
                <a href="#">Menu</a>
                <a href="#">Votre panier</a>
                <a href="#">Se connecter</a>
            </div>
        </div>

    );

}

export const OverlayMenuComponent = () => {

    return (
        <>
            {ReactDOM.createPortal(
                <OverlayComponent over={store.getState().overReducer.over} />, 
                document.getElementById('overlay-menu'))}
        </>
    );

}

export const OverlayMenu = connect(
    null,
    (dispatch) => ({
        changeOver: dispatch(updateOver())
    })
)(OverlayComponent)
